export declare const styles = "\n/* Reset */\n\n.img-stack-root,\n.img-stack-root *, \n.img-stack-root *::before, \n.img-stack-root *::after {\n  box-sizing: border-box;\n  margin: 0;\n  padding: 0;\n}\n\n.img-stack-root button {\n  margin: 0;\n  padding: 0;\n  border: none;\n  background: none;\n  font: inherit;\n  color: inherit;\n  outline: none;\n}\n\n.img-stack-root {\n  --color-text: #2c3e50;\n  --color-background: #ffffff;\n  --color-border: rgba(0, 0, 0, 0.1);\n  --dialog-backdrop: rgba(0, 0, 0, 0.5);\n  --transition-speed: 0.3s;\n  --transition-bounce: cubic-bezier(0.68, -0.6, 0.32, 1.6);\n  --transition-ease: cubic-bezier(0.4, 0, 0.2, 1);\n  --space-3: 1rem;\n  --text-sm: 0.875rem;\n}\n\n@media (prefers-color-scheme: dark) {\n  .img-stack-root {\n    --color-text: #ecf0f1;\n    --color-background: #1a1a1a;\n    --color-border: rgba(255, 255, 255, 0.1);\n    --dialog-backdrop: rgba(0, 0, 0, 0.7);\n  }\n}\n\n.img-stack-root .project-images-stack {\n  position: relative;\n  margin: var(--space-3) 0 0;\n  display: flex;\n  justify-content: center;\n  perspective: 1000px;\n  padding: 0;\n  border: none;\n  background: none;\n  cursor: pointer;\n  transform-origin: center;\n}\n\n@media (hover: none) {\n  .img-stack-root .project-images-stack {\n    touch-action: pan-x;\n  }\n\n  .img-stack-root .project-image-caption {\n    display: none;\n  }\n}\n\n.img-stack-root .project-image-wrapper {\n  position: absolute;\n  width: 100%;\n  height: 100%;\n  transform: rotate(var(--stack-rotation)) translate(var(--stack-translate-x));\n}\n\n.img-stack-root .project-images-stack[data-in-view=\"true\"] .project-image-wrapper {\n  animation: stack-rotate-1 0.8s cubic-bezier(0.68, -0.6, 0.32, 1.6) forwards;\n}\n\n@keyframes stack-rotate-1 {\n  from {\n    transform: rotate(0deg) translate(0);\n  }\n  to {\n    transform: rotate(var(--stack-rotation)) translate(var(--stack-translate-x));\n  }\n}\n\n@keyframes top-elem-hover {\n  to {\n    transform: rotate(0deg) translate(0) scale(1.02);\n    box-shadow: 0 8px 16px #00000026;\n  }\n}\n\n.img-stack-root .project-image {\n  width: 100%;\n  height: 100%;\n  object-fit: cover;\n  border-radius: 8px;\n  border: 1px solid var(--color-border);\n}\n\n.img-stack-root .project-image-caption {\n  position: absolute;\n  bottom: 0;\n  left: 0;\n  right: 0;\n  background: #000000b3;\n  color: #fff;\n  padding: 6px;\n  font-size: var(--text-sm);\n  opacity: 0;\n  transform: translateY(100%);\n  transition: opacity .3s ease, transform .3s cubic-bezier(.34,1.56,.64,1);\n  text-align: center;\n}\n\n.img-stack-root .project-images-stack:hover .project-image-caption {\n  opacity: 1;\n  transform: translateY(0);\n}\n\n.img-stack-root .project-dialog {\n  position: fixed;\n  top: 50%;\n  left: 50%;\n  transform: translate(-50%, -50%) scale(.95);\n  border: none;\n  border-radius: 12px;\n  padding: 0;\n  max-width: 90vw;\n  max-height: 90vh;\n  width: 800px;\n  background: var(--color-background);\n  color: var(--color-text);\n  pointer-events: none;\n  opacity: 0;\n  transition: opacity .2s ease, transform .2s ease;\n}\n\n.img-stack-root .project-dialog.dialog-open {\n  opacity: 1;\n  pointer-events: inherit;\n  transform: translate(-50%, -50%) scale(1);\n}\n\n.img-stack-root .project-dialog::backdrop {\n  background: var(--dialog-backdrop);\n  opacity: 0;\n  transition: opacity var(--transition-speed) var(--transition-ease);\n}\n\n.img-stack-root .project-dialog.dialog-open::backdrop {\n  opacity: 1;\n}\n\n.img-stack-root .dialog-content {\n  padding: 20px;\n  overflow-y: auto;\n  max-height: 90vh;\n}\n\n.img-stack-root .dialog-header {\n  display: flex;\n  justify-content: space-between;\n  align-items: center;\n  margin-bottom: 20px;\n}\n\n.img-stack-root .dialog-title {\n  margin: 0;\n  font-size: 1.5rem;\n}\n\n.img-stack-root .dialog-close {\n  background: none;\n  border: none;\n  font-size: 1.5rem;\n  cursor: pointer;\n  padding: 5px 10px;\n  color: var(--color-text);\n  transition: opacity var(--transition-speed) var(--transition-ease);\n}\n\n.img-stack-root .dialog-close:hover {\n  opacity: 0.7;\n}\n\n.img-stack-root .dialog-body {\n  display: grid;\n  gap: 20px;\n}\n\n.img-stack-root .dialog-body figure {\n  margin: 0;\n}\n\n.img-stack-root .dialog-body img {\n  width: 100%;\n  height: auto;\n  border-radius: 8px;\n  border: 1px solid var(--color-border);\n  object-fit: cover;\n}\n\n.img-stack-root .dialog-body figcaption {\n  margin-top: 8px;\n  text-align: center;\n}\n\n.img-stack-root .project-images-stack:hover .project-image-wrapper:first-child {\n  animation: top-elem-hover .4s cubic-bezier(.34,1.56,.64,1) .1s forwards !important;\n}\n\n@media (hover: none) {\n  .img-stack-root .project-image-caption {\n    display: none;\n  }\n\n  .img-stack-root .project-images-stack:hover .project-image-wrapper:first-child {\n    animation: none !important;\n  }\n\n  .img-stack-root .project-image-wrapper {\n    opacity: 0;\n    transform: translateX(30px) rotate(var(--stack-rotation)) translate(var(--stack-translate-x));\n    transition: all 0.3s cubic-bezier(0.2, 0.8, 0.2, 1);\n    will-change: transform, opacity;\n  }\n\n  .img-stack-root .project-image-wrapper[data-visible=\"true\"] {\n    opacity: 1;\n    transform: translateX(0) rotate(var(--stack-rotation)) translate(var(--stack-translate-x));\n  }\n}\n";
